// src/pages/Chapters/Dallas.js
import React from 'react';
import Header from '../../components/Header';
import Footer from '../../components/Footer';
import banner from '../../assets/images/chapters/Dallas/banner.png';
import '../../assets/fonts/Quicksand-Regular.ttf';
import '../../assets/fonts/Poppins-Regular.ttf';

const Dallas = () => (
    <div>
        <Header />
        {/* Banner Section */}
        <div style={styles.banner}>
            <h1 style={styles.bannerText}>Dallas Chapter</h1>
        </div>
        {/* Main Content Section */}
        <div style={styles.contentSection}>
            <div style={styles.text}>
                The Dallas chapter of Ummati Community is dedicated to building a vibrant network for women in the Dallas area, offering a space where members can find support, encouragement, and lasting friendships. With a focus on community engagement and personal growth, our Dallas chapter provides a range of events, from social gatherings to networking opportunities, helping women in all stages of life feel connected and empowered.
            </div>
        </div>
        <Footer />
    </div>
);

const styles = {
    banner: {
        width: '100%',
        height: '300px', // Adjust height as needed
        backgroundImage: `url(${banner})`,
        backgroundSize: 'cover', // Ensures image covers the entire banner
        backgroundPosition: 'center', // Centers the background image
        position: 'relative', // Allows absolute positioning for inner elements
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    bannerText: {
        color: 'black',
        fontFamily: 'Caprasimo',
        fontSize: '56px',
        position: 'absolute', // Position text absolutely within the banner
        top: '80%', // Position the text two-thirds down within the banner
        transform: 'translateY(-50%)',
        fontWeight: '400',
    },
    contentSection: {
        background: '#F7F5EF',
        padding: '40px 20px',
        textAlign: 'left',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    text: {
        width: '100%',
        maxWidth: '1200px',
        color: 'black',
        fontSize: '22px',
        fontFamily: 'Poppins',
        fontWeight: '400',
        wordWrap: 'break-word',
    },
};

export default Dallas;
