// src/pages/Chapters/Dallas.js
import React from 'react';
import Header from '../../components/Header';
import Footer from '../../components/Footer';
import banner from '../../assets/images/chapters/Dallas/banner.png';
import teamPhotoPlaceholder from '../../assets/images/chapters/teamPhotoPlaceholder.png';
import '../../assets/fonts/Quicksand-Regular.ttf';
import '../../assets/fonts/Poppins-Regular.ttf';

const Dallas = () => (
    <div>
        <Header />
        {/* Banner Section */}
        <div style={styles.banner}>
            <h1 style={styles.bannerText}>Dallas Chapter</h1>
        </div>
        {/* Main Content Section */}
        <div style={styles.contentSection}>
            <div style={styles.text}>
                The Dallas chapter of Ummati Community is dedicated to building a vibrant network for women in the Dallas area, offering a space where members can find support, encouragement, and lasting friendships. With a focus on community engagement and personal growth, our Dallas chapter provides a range of events, from social gatherings to networking opportunities, helping women in all stages of life feel connected and empowered.
            </div>
        </div>
        {/* Meet The Team Section */}
        <div style={styles.teamSection}>
            <div style={styles.teamTitle}>Meet The Team</div>
            <div style={styles.teamGrid}>
                {/* Team Member Placeholders */}
                {[...Array(5)].map((_, index) => (
                    <div key={index} style={styles.teamMember}>
                        <img src={teamPhotoPlaceholder} alt="Team Placeholder" style={styles.teamPhoto} />
                        <div style={styles.teamName}>Name</div>
                        <div style={styles.teamTitleText}>Title</div>
                    </div>
                ))}
            </div>
        </div>
        <Footer />
    </div>
);

const styles = {
    banner: {
        width: '100%',
        height: '300px',
        backgroundImage: `url(${banner})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        position: 'relative',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    bannerText: {
        color: 'black',
        fontFamily: 'Caprasimo',
        fontSize: '56px',
        position: 'absolute',
        top: '80%',
        transform: 'translateY(-50%)',
        fontWeight: '400',
    },
    contentSection: {
        background: '#F7F5EF',
        padding: '40px 20px',
        textAlign: 'center',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'left',
    },
    text: {
        width: '100%',
        maxWidth: '1100px',
        color: 'black',
        fontSize: '22px',
        fontFamily: 'Poppins',
        fontWeight: '400',
        wordWrap: 'break-word',
    },
    teamSection: {
        width: '100%',
        background: 'white',
        padding: '60px 20px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    teamTitle: {
        width: '100%',
        textAlign: 'center',
        color: '#5A4283',
        fontSize: '40px',
        fontFamily: 'Quicksand',
        fontWeight: '700',
        wordWrap: 'break-word',
        marginBottom: '40px',
    },
    teamGrid: {
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', // Responsive grid
        gap: '20px',
        justifyItems: 'center',
        width: '100%',
        maxWidth: '1100px',
    },
    teamMember: {
        textAlign: 'center',
    },
    teamPhoto: {
        width: '150px',
        height: '150px',
        borderRadius: '50%',
        objectFit: 'cover',
        marginBottom: '10px',
    },
    teamName: {
        width: '100%',
        textAlign: 'center',
        color: '#222222',
        fontSize: '20px',
        fontFamily: 'Quicksand',
        fontWeight: '700',
        wordWrap: 'break-word',
        marginBottom: '5px',
    },
    teamTitleText: {
        width: '100%',
        textAlign: 'center',
        color: 'black',
        fontSize: '16px',
        fontFamily: 'Poppins',
        fontWeight: '400',
        wordWrap: 'break-word',
    },
};

export default Dallas;
