// src/pages/Podcast.js
import React from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import bannerImage from '../assets/images/podcasts/bannerImage.png';

const Podcast = () => (
    <div>
      <Header />
      {/* Banner Section */}
    <div style={styles.banner}>
      <h1 style={styles.bannerText}>Interlaced Podcast</h1>
    </div>
      <Footer />
    </div>
  );

  const styles = {
    banner: {
      width: '100%',
      height: '300px', // Adjust height as needed
      backgroundImage: `url(${bannerImage})`,
      backgroundSize: 'cover',       // Ensures image covers the entire banner
      backgroundPosition: 'center',  // Centers the background image
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    bannerText: {
      color: 'white',
      fontSize: '36px',
      fontFamily: 'Quicksand, sans-serif',
      fontWeight: '700',
      textShadow: '2px 2px 5px rgba(0, 0, 0, 0.5)', // Adds shadow for readability
    },
  };

  export default Podcast;
