// src/components/HeroSection.js
import React from 'react';
import leftImage from '../assets/images/hero/left image heroSection.png';
import rightImage from '../assets/images/hero/right image heroSection.png';
import podcastImage from '../assets/images/hero/podcast.png';
import '../assets/fonts/Caprasimo-Regular.ttf';

const HeroSection = () => (
<section style={styles.hero}>
<div>
      <img src={leftImage} alt="Left side illustration" style={styles.leftImage} />
    <div style={styles.content}>
      <h1 style={styles.header}>Welcome to Ummati Community</h1>
      <h3 style={styles.subheader}>We Are One People, One Nation.</h3>
      <p style={styles.paragraph}>
        We are a community where you can meet like-minded women and enjoy the company of so many others just like you! 
        We're bringing together all who love attending a variety of social events and creating a community of friendship and sisterhood.
      </p>
      <button style={styles.button}>Join Us</button>
    </div>
    <img src={rightImage} alt="Right side illustration" style={styles.rightImage} />
    </div>

    <div style={styles.infoSection}>
      <div style={styles.infoItem}>
        <h3>Explore Our Calendar</h3>
        <img src={podcastImage} alt="Calendar illustration" />
      </div>
      <div style={styles.infoItem}>
        <h3>Become A Member</h3>
        <img src={podcastImage} alt="Member illustration" />
      </div>
      <div style={styles.infoItem}>
        <h3>Listen To Our Podcast</h3>
        <img src={podcastImage} alt="Podcast illustration" />
      </div>
    </div>

  </section>
);

const styles = {
  hero: {
    backgroundColor: '#F7F5EF',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '20px',
    position: 'relative',
  },
  content: {
    textAlign: 'center',
    maxWidth: '600px',
  },
  leftImage: {
    position: 'absolute',
    left: '20px',
    top: '50%',
    transform: 'translateY(-50%)',
  },
  rightImage: {
    position: 'absolute',
    right: '20px',
    top: '50%',
    transform: 'translateY(-50%)',
  },
  header: {
    fontFamily: 'Caprasimo, sans-serif',
    fontSize: '42px',
    fontWeight: 'bold',
    margin: 0,
  },
  subheader: {
    fontSize: '24px',
    marginTop: '10px',
  },
  paragraph: {
    fontSize: '18px',
    marginTop: '20px',
  },
  button: {
    marginTop: '30px',
    padding: '10px 20px',
    fontSize: '18px',
    fontWeight: 'bold',
  },
};

export default HeroSection;
