// src/components/HeroSection.js
import React from 'react';
import welcomeBanner from '../assets/images/welcome banner.png';

const HeroSection = () => (
  <section style={styles.hero}>
    <img src={welcomeBanner} alt="Welcome Banner" style={styles.bannerImage} />

    <div style={styles.contentContainer}>
      <h1 style={styles.title}>We Are One People, One Nation.</h1>
      <p style={styles.subtitle}>
        We are a community where you can meet like-minded women and enjoy the company of so many others just like you! We're bringing together all who love attending a variety of social events and creating a community of friendship and sisterhood.
      </p>

      {/* Call to Action Button */}
      <div style={styles.buttonContainer}>
        <div style={styles.ctaButton}>
          <span style={styles.buttonText}>Join Our Community</span>
        </div>
      </div>
    </div>
  </section>
);

const styles = {
  hero: {
    width: '100%',
    height: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    position: 'relative',
    overflow: 'hidden',
  },
  bannerImage: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    position: 'absolute',
    top: 0,
    left: 0,
    zIndex: 1,
  },
  contentContainer: {
    position: 'relative',
    zIndex: 2,
    textAlign: 'center',
    color: '#F7F5EF',
    padding: '20px',
    backgroundColor: 'rgba(0, 0, 0, 0.5)', // Background overlay for readability
    borderRadius: '10px',
    maxWidth: '800px',
  },
  title: {
    fontSize: '2rem',
    fontFamily: 'Quicksand',
    fontWeight: 700,
    marginBottom: '20px',
  },
  subtitle: {
    fontSize: '1.25rem',
    fontFamily: 'Poppins',
    fontWeight: 400,
    lineHeight: '1.5',
    marginBottom: '30px',
  },
  buttonContainer: {
    width: '100%',
    display: 'flex',
    justifyContent: 'center',
  },
  ctaButton: {
    backgroundColor: '#78B27B',
    borderRadius: 10,
    padding: '12px 24px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    cursor: 'pointer',
  },
  buttonText: {
    color: '#F7F5EF',
    fontSize: 20,
    fontFamily: 'Quicksand',
    fontWeight: 700,
    textAlign: 'center',
  },
};

export default HeroSection;
