import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import logo from '../assets/icons/logo.png';
import '../assets/fonts/Quicksand-Regular.ttf';
import '../assets/fonts/Poppins-Regular.ttf';

const Header = () => {
    const location = useLocation();
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
    const [hoveredItem, setHoveredItem] = useState(null);

    const navItems = [
        { name: 'Events', path: '/events' },
        { name: 'Podcast', path: '/podcast' },
        { name: 'Membership', path: '/membership' },
        { name: 'Chapters', path: '/chapters' },
        { name: 'About', path: '/about' },
        { name: 'Contact', path: '/contact' },
    ];

    const chapters = [
        { name: 'Dallas', path: '/chapters/dallas' },
        { name: 'Fort Worth', path: '/chapters/fort-worth' },
        { name: 'Houston', path: '/chapters/houston' },
        { name: 'Little Rock', path: '/chapters/little-rock' },
    ];

    const toggleDropdown = () => {
        setDropdownOpen(!dropdownOpen);
    };

    const closeDropdown = () => {
        setDropdownOpen(false);
        setHoveredItem(null);
    };

    const toggleMobileMenu = () => {
        setMobileMenuOpen(!mobileMenuOpen);
    };

    const isActive = (path) => location.pathname === path;

    return (
        <header style={styles.header}>
            {/* Logo and Hamburger Menu */}
            <div style={styles.logoContainer}>
                <Link to="/" style={styles.titleContainer}>
                    <span style={styles.titleText}>UMMATI C</span>
                    <img src={logo} alt="Logo" style={styles.logo} />
                    <span style={styles.titleText}>MMUNITY</span>
                </Link>
                <button style={styles.hamburger} onClick={toggleMobileMenu}>
                    <div style={styles.bar}></div>
                    <div style={styles.bar}></div>
                    <div style={styles.bar}></div>
                </button>
            </div>

            {/* Navigation Links */}
            <nav
                style={{
                    ...styles.navLinks,
                    ...(mobileMenuOpen ? styles.navLinksOpen : {}),
                }}
            >
                {navItems.map((item) => {
                    if (item.name === 'Chapters') {
                        return (
                            <div
                                key={item.name}
                                style={styles.dropdownContainer}
                                onMouseEnter={toggleDropdown}
                                onMouseLeave={closeDropdown}
                            >
                                <Link
                                    to={item.path}
                                    style={{
                                        ...styles.navItem,
                                        ...(isActive(item.path) ? styles.activeNavItem : {}),
                                    }}
                                >
                                    {item.name}
                                </Link>
                                {dropdownOpen && (
                                    <div style={styles.dropdown}>
                                        {chapters.map((chapter) => (
                                            <Link
                                                key={chapter.name}
                                                to={chapter.path}
                                                style={{
                                                    ...styles.dropdownItem,
                                                    ...(hoveredItem === chapter.name
                                                        ? styles.dropdownItemHover
                                                        : {}),
                                                }}
                                                onMouseEnter={() => setHoveredItem(chapter.name)}
                                                onMouseLeave={() => setHoveredItem(null)}
                                            >
                                                {chapter.name}
                                            </Link>
                                        ))}
                                    </div>
                                )}
                            </div>
                        );
                    }
                    return (
                        <Link
                            key={item.name}
                            to={item.path}
                            style={{
                                ...styles.navItem,
                                ...(isActive(item.path) ? styles.activeNavItem : {}),
                            }}
                        >
                            {item.name}
                        </Link>
                    );
                })}
                <div style={styles.loginButton}>
                    <span style={styles.loginButtonText}>Login</span>
                </div>
            </nav>
        </header>
    );
};

const styles = {
    header: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        background: 'white',
        padding: '15px 20px',
        width: '100%',
        position: 'relative',
        flexWrap: 'wrap',
    },
    logoContainer: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        width: '100%',
    },
    titleContainer: {
        display: 'flex',
        alignItems: 'center',
        gap: '5px',
    },
    titleText: {
        color: '#5A4283',
        fontSize: '1.75rem',
        fontFamily: 'Quicksand',
        fontWeight: 700,
        whiteSpace: 'nowrap',
        margin: 0,
    },
    logo: {
        width: 'clamp(30px, 5vw, 50px)',
        height: 'clamp(30px, 5vw, 50px)',
        marginTop: '-1px',
    },
    hamburger: {
        background: 'none',
        border: 'none',
        cursor: 'pointer',
        display: 'none', // Hidden for larger screens
        flexDirection: 'column',
        justifyContent: 'space-between',
        height: '24px',
        width: '30px',
    },
    bar: {
        height: '4px',
        width: '100%',
        backgroundColor: '#5A4283',
    },
    navLinks: {
        display: 'flex',
        gap: '20px',
        alignItems: 'center',
        flexWrap: 'wrap',
    },
    navLinksOpen: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        background: 'white',
        position: 'absolute',
        top: '100%',
        left: 0,
        width: '100%',
        zIndex: 1000,
        padding: '10px 0',
    },
    navItem: {
        color: '#5A4283',
        fontSize: '1rem',
        fontFamily: 'Quicksand',
        cursor: 'pointer',
        textAlign: 'center',
        whiteSpace: 'nowrap',
    },
    activeNavItem: {
        fontWeight: '700',
    },
    dropdownContainer: {
        position: 'relative',
    },
    dropdown: {
        position: 'absolute',
        top: '100%',
        left: 0,
        background: 'white',
        border: '1px solid #ECE7DA',
        borderRadius: '8px',
        zIndex: 10,
        width: '200px',
    },
    dropdownItem: {
        width: '100%',
        padding: '12px',
        color: '#1E1E1E',
        fontSize: '12px',
        fontFamily: 'Poppins',
        fontWeight: '400',
        textDecoration: 'none',
        display: 'inline-flex',
        justifyContent: 'flex-start',
        alignItems: 'center',
    },
    dropdownItemHover: {
        background: '#D9F4DA',
    },
    loginButton: {
        padding: '8px 16px',
        backgroundColor: '#78B27B',
        borderRadius: 40,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    loginButtonText: {
        color: '#D9F4DA',
        fontSize: '1rem',
        fontFamily: 'Quicksand',
        fontWeight: 700,
    },
};

export default Header;
