import React, { useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import { Box, Button, Typography, Checkbox, FormControlLabel } from "@mui/material";

const localizer = momentLocalizer(moment);

const Events = () => {
  const [view, setView] = useState("week");
  const [listView, setListView] = useState(false);
  const events = [
    {
      title: "Pot party at Danny's",
      start: new Date(2024, 10, 4, 9, 0),
      end: new Date(2024, 10, 4, 14, 0),
    },
    {
      title: "Fill (1037) | Hug",
      start: new Date(2024, 10, 4, 18, 0),
      end: new Date(2024, 10, 4, 19, 0),
    },
  ];

  return (
    <Box
      sx={{
        backgroundColor: "#F7F5EF",
        minHeight: "100vh",
      }}
    >
      {/* Header */}
      <Box
        sx={{
          width: "100%",
          height: "220px",
          backgroundColor: "#FFF4E5",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <Typography
          variant="h1"
          sx={{
            color: "#3D3D3C",
            fontFamily: "Caprasimo",
            fontSize: { xs: "36px", sm: "48px", md: "56px" },
          }}
        >
          Events
        </Typography>
      </Box>

      {/* Content Section */}
      <Box sx={{ display: "flex", padding: "24px" }}>
        {/* Sidebar */}
        <Box sx={{ width: "20%", marginRight: "16px" }}>
          <Typography variant="h6" sx={{ marginBottom: "16px" }}>
            Filter Events
          </Typography>
          <Typography sx={{ marginBottom: "8px" }}>State</Typography>
          <FormControlLabel control={<Checkbox />} label="Texas" />
          <FormControlLabel control={<Checkbox />} label="Arkansas" />
        </Box>

        {/* Main Section */}
        <Box sx={{ width: "80%" }}>
          {/* Toggle View Buttons */}
          <Box sx={{ display: "flex", justifyContent: "space-between", marginBottom: "16px" }}>
            <Button
              variant="contained"
              sx={{
                backgroundColor: !listView ? "#5A4283" : "#ddd",
                color: !listView ? "#FFF" : "#000",
              }}
              onClick={() => setListView(false)}
            >
              Calendar View
            </Button>
            <Button
              variant="contained"
              sx={{
                backgroundColor: listView ? "#5A4283" : "#ddd",
                color: listView ? "#FFF" : "#000",
              }}
              onClick={() => setListView(true)}
            >
              List View
            </Button>
          </Box>

          {/* Calendar or List View */}
          {!listView ? (
            <Calendar
              localizer={localizer}
              events={events}
              defaultView={view}
              views={["day", "week", "month"]}
              startAccessor="start"
              endAccessor="end"
              style={{ height: 600 }}
              onView={(newView) => setView(newView)}
            />
          ) : (
            <Box>
              {events.map((event, index) => (
                <Box
                  key={index}
                  sx={{
                    border: "1px solid #ddd",
                    padding: "16px",
                    borderRadius: "8px",
                    marginBottom: "8px",
                    backgroundColor: "#FFF",
                  }}
                >
                  <Typography variant="h6">{event.title}</Typography>
                  <Typography>
                    {moment(event.start).format("dddd, MMMM Do YYYY, h:mm a")} -{" "}
                    {moment(event.end).format("h:mm a")}
                  </Typography>
                </Box>
              ))}
            </Box>
          )}
        </Box>
      </Box>
    </Box>
  );
};

export default Events;
