import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import logo from '../assets/icons/logo.png';
import '../assets/fonts/Quicksand-Regular.ttf';
import '../assets/fonts/Poppins-Regular.ttf';

const Header = () => {
    const location = useLocation();
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const [hamburgerOpen, setHamburgerOpen] = useState(false);
    const [hoveredItem, setHoveredItem] = useState(null);
    const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

    const navItems = [
        { name: 'Events', path: '/events' },
        { name: 'Podcast', path: '/podcast' },
        { name: 'Membership', path: '/membership' },
        { name: 'Chapters', path: '/chapters' },
        { name: 'About', path: '/about' },
        { name: 'Contact', path: '/contact' },
    ];

    const chapters = [
        { name: 'Dallas, TX', path: '/chapters/dallas' },
        { name: 'Fort Worth, TX', path: '/chapters/fort-worth' },
        { name: 'Houston, TX', path: '/chapters/houston' },
        { name: 'Little Rock, AR', path: '/chapters/little-rock' },
    ];

    const toggleDropdown = () => {
        setDropdownOpen(!dropdownOpen);
    };

    const toggleHamburger = () => {
        setHamburgerOpen(!hamburgerOpen);
    };

    const closeDropdown = () => {
        setDropdownOpen(false);
        setHoveredItem(null);
    };

    const isActive = (path) => location.pathname === path;

    // Handle window resize to toggle mobile view
    useEffect(() => {
        const handleResize = () => setIsMobile(window.innerWidth <= 768);
        window.addEventListener('resize', handleResize);

        return () => window.removeEventListener('resize', handleResize);
    }, []);

    return (
        <header style={styles.header}>
            {/* Logo Section */}
            <Link to="/" style={styles.titleContainer}>
                <span style={styles.titleText}>UMMATI C</span>
                <img src={logo} alt="Logo" style={styles.logo} />
                <span style={styles.titleText}>MMUNITY</span>
            </Link>

            {/* Hamburger Menu for Mobile */}
            {isMobile && (
                <div style={styles.hamburger} onClick={toggleHamburger}>
                    <div style={styles.hamburgerLine}></div>
                    <div style={styles.hamburgerLine}></div>
                    <div style={styles.hamburgerLine}></div>
                </div>
            )}

            {/* Navigation Links */}
            {(hamburgerOpen || !isMobile) && (
                <div style={isMobile ? styles.mobileNav : styles.rightSection}>
                    <nav style={styles.navLinks}>
                        {navItems.map((item) => {
                            if (item.name === 'Chapters') {
                                return (
                                    <div
                                        key={item.name}
                                        style={styles.dropdownContainer}
                                        onMouseEnter={!isMobile ? toggleDropdown : undefined}
                                        onMouseLeave={!isMobile ? closeDropdown : undefined}
                                    >
                                        <div
                                            onClick={isMobile ? toggleDropdown : undefined}
                                            style={{
                                                ...styles.navItem,
                                                ...(isActive(item.path) ? styles.activeNavItem : {}),
                                            }}
                                        >
                                            {item.name}
                                        </div>
                                        {(dropdownOpen || isMobile) && (
                                            <div style={styles.dropdown}>
                                                {chapters.map((chapter) => (
                                                    <Link
                                                        key={chapter.name}
                                                        to={chapter.path}
                                                        style={{
                                                            ...styles.dropdownItem,
                                                            ...(hoveredItem === chapter.name
                                                                ? styles.dropdownItemHover
                                                                : {}),
                                                        }}
                                                        onMouseEnter={() => setHoveredItem(chapter.name)}
                                                        onMouseLeave={() => setHoveredItem(null)}
                                                        onClick={isMobile ? () => setHamburgerOpen(false) : undefined}
                                                    >
                                                        {chapter.name}
                                                    </Link>
                                                ))}
                                            </div>
                                        )}
                                    </div>
                                );
                            }
                            return (
                                <Link
                                    key={item.name}
                                    to={item.path}
                                    style={{
                                        ...styles.navItem,
                                        ...(isActive(item.path) ? styles.activeNavItem : {}),
                                    }}
                                    onClick={isMobile ? () => setHamburgerOpen(false) : undefined}
                                >
                                    {item.name}
                                </Link>
                            );
                        })}
                    </nav>

                    {/* Login Button */}
                    <div style={styles.loginButton}>
                        <span style={styles.loginButtonText}>Login</span>
                    </div>
                </div>
            )}
        </header>
    );
};

const styles = {
    header: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        background: 'white',
        padding: '20px',
        width: '100%',
        flexWrap: 'wrap',
        position: 'relative',
    },
    titleContainer: {
        display: 'flex',
        alignItems: 'center',
        gap: '5px',
    },
    titleText: {
        color: '#5A4283',
        fontSize: '1.75rem',
        fontFamily: 'Quicksand',
        fontWeight: 700,
        whiteSpace: 'nowrap',
    },
    logo: {
        width: 'clamp(30px, 5vw, 50px)',
        height: 'clamp(30px, 5vw, 50px)',
    },
    hamburger: {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        width: '30px',
        height: '25px',
        cursor: 'pointer',
    },
    hamburgerLine: {
        width: '100%',
        height: '4px',
        backgroundColor: '#5A4283',
    },
    rightSection: {
        display: 'flex',
        alignItems: 'center',
        gap: '20px',
    },
    mobileNav: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        width: '100%',
        gap: '10px',
        marginTop: '10px',
    },
    navLinks: {
        display: 'flex',
        gap: '20px',
        flexWrap: 'wrap',
        flexDirection: 'row',
    },
    navItem: {
        color: '#5A4283',
        fontSize: '1rem',
        fontFamily: 'Quicksand',
        cursor: 'pointer',
        textAlign: 'center',
    },
    activeNavItem: {
        fontWeight: '700',
    },
    dropdownContainer: {
        position: 'relative',
    },
    dropdown: {
        position: isMobile ? 'relative' : 'absolute',
        top: isMobile ? '0' : '100%',
        left: '0',
        background: 'white',
        border: '1px solid #ECE7DA',
        boxShadow: isMobile ? 'none' : '0px 4px 10px rgba(0, 0, 0, 0.1)',
        borderRadius: '8px',
        zIndex: 10,
        display: 'flex',
        flexDirection: 'column',
    },
    dropdownItem: {
        padding: '12px',
        color: '#1E1E1E',
        fontSize: '12px',
        fontFamily: 'Poppins',
        textDecoration: 'none',
    },
    dropdownItemHover: {
        background: '#D9F4DA',
    },
    loginButton: {
        padding: '8px 16px',
        backgroundColor: '#78B27B',
        borderRadius: 40,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    loginButtonText: {
        color: '#D9F4DA',
        fontSize: '1rem',
        fontFamily: 'Quicksand',
        fontWeight: 700,
    },
};

export default Header;
