// src/components/HeroSection.js
import React from 'react';
import leftImage from '../assets/images/homepage/hero/left image heroSection.png';
import rightImage from '../assets/images/homepage/hero/right image heroSection.png';
import podcastImage from '../assets/images/homepage/hero/podcast.png';
import '../assets/fonts/Caprasimo-Regular.ttf';
import '../assets/fonts/Quicksand-Regular.ttf';
import '../assets/fonts/Poppins-Regular.ttf';

const styles = {
  hero: {
    backgroundColor: '#F7F5EF',
    width: '100vw',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '20px',
    margin: '0',
  },
  headerRow: {
    width: '100vw',
    textAlign: 'center',
  },
  middleRow: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    width: '100vw',
    marginTop: '20px',
  },
  section: {
    flex: '1',
    textAlign: 'center',
    padding: '10px',
  },
  image: {
    width: '100%',
    maxWidth: '200px',
    height: 'auto',
  },
  header: {
    fontFamily: 'Caprasimo',
    fontSize: '56px',
    margin: 0,
  },
  subheader: {
    width: '100%',
    textAlign: 'center',
    color: 'black',
    fontSize: '32px',
    fontFamily: 'Quicksand',
    fontWeight: '700',
  },
};

const HeroSection = () => {
  return (
    <div style={styles.hero}>
      <div style={styles.headerRow}>
        <h1 style={styles.header}>Welcome to Our Community</h1>
        <h2 style={styles.subheader}>Connecting People, Building Relationships</h2>
      </div>
      <div style={styles.middleRow}>
        <div style={styles.section}>
          <h3>Explore Our Calendar</h3>
          <img src={leftImage} alt="Calendar illustration" style={styles.image} />
        </div>
        <div style={styles.section}>
          <h3>Become A Member</h3>
          <img src={rightImage} alt="Member illustration" style={styles.image} />
        </div>
        <div style={styles.section}>
          <h3>Listen To Our Podcast</h3>
          <img src={podcastImage} alt="Podcast illustration" style={styles.image} />
        </div>
      </div>
    </div>
  );
};

export default HeroSection;
