import React, { useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import Header from "../components/Header";
import Footer from "../components/Footer";
import bannerImage from "../assets/images/Events/banner.png";
import {
  Box,
  Button,
  Typography,
  FormControlLabel,
  Checkbox,
} from "@mui/material";

const localizer = momentLocalizer(moment);

const Events = () => {
  const [view, setView] = useState("week"); // Toggle between week and day view
  const [events, setEvents] = useState([
    {
      title: "Pot party at Danny's",
      start: new Date(2024, 10, 4, 9, 0), // Nov 4th, 9am
      end: new Date(2024, 10, 4, 14, 0), // Nov 4th, 2pm
    },
    {
      title: "Fill (1037) | Hug",
      start: new Date(2024, 10, 4, 18, 0), // Nov 4th, 6pm
      end: new Date(2024, 10, 4, 19, 0), // Nov 4th, 7pm
    },
    {
      title: "Fill (1037) | Hug",
      start: new Date(2024, 10, 4, 18, 0), // Nov 4th, 6pm
      end: new Date(2024, 10, 4, 19, 0), // Nov 4th, 7pm
    },
    {
      title: "Fill (1037) | Hug",
      start: new Date(2024, 10, 4, 18, 0), // Nov 4th, 6pm
      end: new Date(2024, 10, 4, 19, 0), // Nov 4th, 7pm
    },
    {
      title: "Fill (1037) | Hug",
      start: new Date(2024, 10, 4, 18, 0), // Nov 4th, 6pm
      end: new Date(2024, 10, 4, 19, 0), // Nov 4th, 7pm
    },
    {
      title: "Fill (1037) | Hug",
      start: new Date(2024, 10, 4, 18, 0), // Nov 4th, 6pm
      end: new Date(2024, 10, 4, 19, 0), // Nov 4th, 7pm
    },
    {
      title: "Fill (1037) | Hug",
      start: new Date(2024, 10, 4, 18, 0), // Nov 4th, 6pm
      end: new Date(2024, 10, 4, 19, 0), // Nov 4th, 7pm
    },
    {
      title: "Fill (1037) | Hug",
      start: new Date(2024, 10, 4, 18, 0), // Nov 4th, 6pm
      end: new Date(2024, 10, 4, 19, 0), // Nov 4th, 7pm
    },
  ]);
  const [listView, setListView] = useState(false);

  const handleToggleView = () => setListView(!listView);

  return (
    <Box
      sx={{
        backgroundColor: "#F7F5EF",
        minHeight: "100vh",
      }}
    >
      {/* Header */}

      <Header />
      <Box
        sx={{
          width: "100%",
          height: "220px",
          backgroundImage: `url(${bannerImage})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          position: "relative",
        }}
      >
        <Typography
          variant="h1"
          sx={{
            color: "#3D3D3C",
            fontFamily: "Caprasimo",
            fontSize: { xs: "36px", sm: "48px", md: "56px" },
            position: "absolute",
            top: "80%",
            transform: "translateY(-50%)",
          }}
        >
          Events
        </Typography>
      </Box>

      {/* Content Section */}
      <Box sx={{ display: "flex", padding: "24px" }}>
        {/* Sidebar */}
        <Box
          sx={{
            width: "20%",
            height: "400px",
            marginRight: "16px",
            backgroundColor: "#5A4283",
          }}
        >
          {/* <Typography variant="h6" sx={{ fontFamily: "Poppins", color: "#FFFF", marginBottom: "16px" }}>
            Filter Events
          </Typography> */}
          <Typography
            sx={{ fontFamily: "Poppins", color: "#FFFF", marginBottom: "8px" }}
          >
            State
          </Typography>
          <FormControlLabel control={<Checkbox />} label="Texas" />
          <FormControlLabel control={<Checkbox />} label="Arkansas" />
          <Typography
            sx={{
              fontFamily: "Poppins",
              color: "#FFFF",
              marginBottom: "8px",
              marginTop: "16px",
            }}
          >
            City
          </Typography>
          <FormControlLabel control={<Checkbox />} label="Dallas" />
          <FormControlLabel control={<Checkbox />} label="Houston" />
        </Box>

        {/* Main Section */}
        <Box sx={{ width: "80%" }}>
          {/* Toggle View Buttons */}
          <Box
            sx={{
              display: "flex",
              justifyContent: "space-between",
              marginBottom: "16px",
            }}
          >
            <Button
              variant="contained"
              sx={{
                backgroundColor: !listView ? "#5A4283" : "#ddd",
                color: !listView ? "#FFF" : "#000",
              }}
              onClick={() => setListView(false)}
            >
              Calendar View
            </Button>
            <Button
              variant="contained"
              sx={{
                backgroundColor: listView ? "#5A4283" : "#ddd",
                color: listView ? "#FFF" : "#000",
              }}
              onClick={() => setListView(true)}
            >
              List View
            </Button>
          </Box>

          {/* Calendar or List View */}
          {!listView ? (
            <Calendar
              localizer={localizer}
              events={events}
              defaultView={view}
              views={["day", "week", "month"]}
              startAccessor="start"
              endAccessor="end"
              style={{ height: 600 }}
              onView={(newView) => setView(newView)}
            />
          ) : (
            <Box
              sx={{
                height: 600, // Set a fixed height for the list
                overflowY: "auto", // Enable vertical scrolling
                overflowX: "hidden", // Prevent horizontal scrolling if not needed
                padding: "16px", // Optional padding around the list
                backgroundColor: "#f9f9f9", // Optional background color for contrast
                borderRadius: "8px", // Optional rounded edges
                boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)", // Optional shadow for better aesthetics
              }}
            >
              {events.map((event, index) => (
                <Box
                  key={index}
                  sx={{
                    border: "1px solid #ddd",
                    padding: "16px",
                    borderRadius: "8px",
                    marginBottom: "8px",
                    backgroundColor: "#FFF",
                  }}
                >
                  <Typography variant="h6">{event.title}</Typography>
                  <Typography>
                    {moment(event.start).format("dddd, MMMM Do YYYY, h:mm a")} -{" "}
                    {moment(event.end).format("h:mm a")}
                  </Typography>
                </Box>
              ))}
            </Box>
          )}
        </Box>
      </Box>

      <Footer />
    </Box>
  );
};

export default Events;
