// src/pages/Podcast.js
import React from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import bannerImage from '../assets/images/podcasts/bannerImage.png';

const Podcast = () => (
    <div>
      <Header />
      {/* Banner Section */}
    <div style={styles.banner}>
      <h1 style={styles.bannerText}>Interlaced Podcast</h1>
    </div>

    <div style={styles.contentSection}>
      {/* Add content for this section here */}
      <h2 style={styles.sectionTitle}>About the Podcast</h2>
      <p style={styles.sectionText}>
        Welcome to the Interlaced Podcast! Join us as we dive into discussions that inspire, inform, and empower women in various stages of life. From insightful conversations to exciting stories, our podcast aims to create a supportive community.
      </p>
    </div>
      <Footer />
    </div>
  );

  const styles = {
    banner: {
      width: '100%',
      height: '300px', // Adjust height as needed
      backgroundImage: `url(${bannerImage})`,
      backgroundSize: 'cover',       // Ensures image covers the entire banner
      backgroundPosition: 'center',  // Centers the background image
      position: 'relative', // Allows absolute positioning for inner elements
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    },
    bannerText: {
      color: 'black',
      fontFamily: 'Caprasimo',
      fontSize: '56px',
      position: 'absolute',     // Position text absolutely within the banner
      top: '80%',               // Position the text two-thirds down within the banner
      transform: 'translateY(-50%)',
    },

    contentSection: {
        background: '#F7F5EF',
        padding: '40px 20px', // Add padding for spacing
        textAlign: 'center',  // Center-aligns content
      },
  };

  export default Podcast;
